import React, { useEffect, useState } from "react";
import { fetchProducts } from "./services/api";
import { ProductList } from "./components/ProductList";
import "./index.css"; // Ensure global styles are applied

function App() {
  const [amazonLimit, setAmazonLimit] = useState<number>(() => parseInt(localStorage.getItem("amazonLimit") || "8", 10));
  const [ebayLimit, setEbayLimit] = useState<number>(() => parseInt(localStorage.getItem("ebayLimit") || "8", 10));
  const [amazonItems, setAmazonItems] = useState<any[]>([]);
  const [ebayItems, setEbayItems] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    load();
  }, [amazonLimit, ebayLimit]);

  async function load() {
    setLoading(true);
    try {
      const [a, e] = await Promise.all([
        fetchProducts("amazon", "jaws", amazonLimit),
        fetchProducts("ebay", "jaws", ebayLimit)
      ]);
      setAmazonItems(a);
      setEbayItems(e);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  }

  function saveSettings() {
    localStorage.setItem("amazonLimit", String(amazonLimit));
    localStorage.setItem("ebayLimit", String(ebayLimit));
    load();
  }

  return (
    <div style={{
      maxWidth: 1100,
      margin: "0 auto",
      padding: "16px",
    }}>
      <header style={{ textAlign: "center", marginBottom: "24px" }}>
        <h1 style={{ fontSize: "1.8rem" }}>ðŸ¦ˆ JAWS Merchandise</h1>
        <p style={{ color: "#555" }}>Affiliate listings from Amazon and eBay</p>
      </header>

      <section style={{
        display: "flex",
        flexDirection: "column",
        gap: "12px",
        marginBottom: 24,
      }}>
        <h2 style={{ fontSize: "1.25rem" }}>Settings</h2>
        <div style={{
          display: "flex",
          flexWrap: "wrap",
          gap: "12px",
          alignItems: "center"
        }}>
          <label>
            Amazon listings:
            <input
              type="number"
              min={1}
              max={50}
              value={amazonLimit}
              onChange={e => setAmazonLimit(parseInt(e.target.value || "1", 10))}
              style={{ marginLeft: 6, padding: "4px", width: 70 }}
            />
          </label>
          <label>
            eBay listings:
            <input
              type="number"
              min={1}
              max={50}
              value={ebayLimit}
              onChange={e => setEbayLimit(parseInt(e.target.value || "1", 10))}
              style={{ marginLeft: 6, padding: "4px", width: 70 }}
            />
          </label>
          <button onClick={saveSettings}>Save</button>
        </div>
      </section>

      <section>
        <h2 style={{ fontSize: "1.25rem" }}>Amazon â€” Most Popular</h2>
        {loading ? <div>Loadingâ€¦</div> : <ProductList items={amazonItems} />}
      </section>

      <section style={{ marginTop: 30 }}>
        <h2 style={{ fontSize: "1.25rem" }}>eBay â€” Most Popular</h2>
        {loading ? <div>Loadingâ€¦</div> : <ProductList items={ebayItems} />}
      </section>

      <footer style={{
        marginTop: 30,
        fontSize: 12,
        textAlign: "center",
        color: "#666"
      }}>
        We may earn a commission from links on this page.
      </footer>
    </div>
  );
}

export default App;
