import React from "react";

interface Product {
  title: string;
  image: string;
  asin?: string;
  id?: string;
  url?: string;
}

interface Props {
  items: Product[];
}

export const ProductList: React.FC<Props> = ({ items }) => {
  if (!items || items.length === 0) {
    return <p className="text-gray-600 text-center">No items found.</p>;
  }

  return (
    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
      {items.map((item, index) => {
        // Build URL if missing (for fallbacks)
        const url =
          item.url ||
          (item.asin
            ? `https://www.amazon.com/dp/${item.asin}?tag=affiliateID`
            : item.id
            ? `https://www.ebay.com/itm/${item.id}`
            : "#");

        return (
          <a
            key={item.asin || item.id || index}
            href={url}
            target="_blank"
            rel="noopener noreferrer"
            className="block bg-white rounded-2xl shadow hover:shadow-xl transition-transform hover:-translate-y-1 overflow-hidden text-center"
          >
            <img
              src={item.image}
              alt={item.title}
              className="w-full h-48 object-contain p-2"
              loading="lazy"
            />
            <div className="p-2">
              <p className="text-sm font-medium text-gray-800 mb-2 line-clamp-2">{item.title}</p>
              <button className="bg-blue-600 text-white text-xs px-3 py-1 rounded-full hover:bg-blue-700 transition">
                Shop Now
              </button>
            </div>
          </a>
        );
      })}
    </div>
  );
};
