// src/components/ProductList.tsx
import React from "react";

export const ProductList = ({ items }: { items: any[] }) => {
  if (!items?.length) return <div>No items available.</div>;

  const videos = [
    "https://www.youtube.com/embed/1JzMKO7_v14?si=7Yhj_W_9Idr1sRDU",
    "https://www.youtube.com/embed/_th7MXgiYpQ?si=sPdZHQQkzIvQrswx",
    "https://www.youtube.com/embed/ZOV_sYXToNg?si=z6HN2nvfcryKXWsC",
    "https://www.youtube.com/embed/LQ1GDjVjZL8?si=cMFCQM3vy_hEL8mv",
  ];

  return (
    <div
      style={{
        display: "flex",
        flexWrap: "wrap",
        alignItems: "flex-start",
        gap: "24px",
      }}
    >
      {/* Product Grid */}
      <div
        style={{
          flex: "2 1 60%",
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(180px, 1fr))",
          gap: "16px",
        }}
      >
        {items.map((item, i) => (
          <a
            key={i}
            href={item.link}
            target="_blank"
            rel="noopener noreferrer"
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              textDecoration: "none",
              background: "white",
              borderRadius: 12,
              padding: 12,
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              transition: "transform 0.15s ease-in-out, box-shadow 0.15s ease-in-out",
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = "scale(1.03)";
              e.currentTarget.style.boxShadow = "0 4px 12px rgba(0,0,0,0.15)";
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = "scale(1.0)";
              e.currentTarget.style.boxShadow = "0 2px 6px rgba(0,0,0,0.1)";
            }}
          >
            <img
              src={item.image}
              alt={item.title}
              style={{
                width: "100%",
                borderRadius: 8,
                objectFit: "contain",
                maxHeight: 200,
              }}
            />
            <div
              style={{
                marginTop: 8,
                fontWeight: 600,
                fontSize: 14,
                textAlign: "center",
                color: "#111",
              }}
            >
              {item.title}
            </div>
            <button
              style={{
                marginTop: 8,
                backgroundColor: "#2563eb",
                color: "white",
                border: "none",
                padding: "8px 12px",
                borderRadius: 8,
                cursor: "pointer",
                fontWeight: 500,
              }}
            >
              Shop Now â†’
            </button>
          </a>
        ))}
      </div>

      {/* Sidebar */}
      <aside
        style={{
          flex: "1 1 35%",
          display: "flex",
          flexDirection: "column",
          gap: "16px",
        }}
      >
        <h2
          style={{
            marginBottom: 8,
            fontSize: 20,
            fontWeight: 700,
            textAlign: "center",
            color: "#1e40af",
          }}
        >
          ðŸŽ¥ Jaws 50th Anniversary Videos
        </h2>

        {videos.map((url, index) => (
          <div
            key={index}
            style={{
              position: "relative",
              width: "100%",
              paddingBottom: "56.25%", // 16:9 ratio
              borderRadius: 12,
              overflow: "hidden",
              boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
            }}
          >
            <iframe
              src={url}
              title={`Jaws video ${index + 1}`}
              style={{
                position: "absolute",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                border: "none",
              }}
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </div>
        ))}
      </aside>
    </div>
  );
};
